Action.CSharpCodeSnippet:
//Praxi Solutions, Inc.
//Sales Order Detail - Load Actions


//Enable/Disable Fields
Sage.Entity.Interfaces.ISalesOrder salesOrder = BindingSource.Current as Sage.Entity.Interfaces.ISalesOrder;
if (salesOrder != null)
{
	bool closed = salesOrder.Status.Equals(GetLocalResourceObject("SalesOrderStatus_Closed"));
	lueAccount.Enabled = !closed;
	usrAccountManager.Enabled = !closed;
	lueOpportunity.Enabled = !closed;
	dtpDateCreated.Enabled = !closed;
	lueRequestedBy.Enabled = !closed;
	dtpOrderDate.Enabled = !closed;
	dtpPromisedDate.Enabled = !closed;
	pklType.Enabled = !closed;
	pklStatus.Enabled = !closed;
	txtCustPO.Enabled = !closed;
	btnSaveSalesOrder.Visible = !closed;
	btnReset.Visible = !closed;
	btnDelete.Visible = !closed;
	txtComments.Enabled = !closed;
}
if (salesOrder.Actual==true){
	this.lblAnticipated.Text="Actual Order";
}else{
	this.lblAnticipated.Text="Anticipated Order";}

Sage.SalesLogix.Security.SLXUserService usersvc = (Sage.SalesLogix.Security.SLXUserService)Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Security.IUserService>();
Sage.Entity.Interfaces.IUser user = usersvc.GetUser();		
int team =0;
using (NHibernate.ISession session = new Sage.Platform.Orm.SessionScopeWrapper()){
        		string sql = "select count(*) from seccodejoins sj join seccode s on s.seccodeid=sj.parentseccodeid where sj.childseccodeid in (select defaultseccodeid from usersecurity where userid='"+user.Id.ToString()+"') and s.seccodetype='G' and s.seccodedesc='Training'";
				team = session.CreateSQLQuery(sql).UniqueResult<int>();			
			}		

if (team>0 || user.Id.ToString().Trim()=="ADMIN"){
		dtpOrderDate.ReadOnly=false;
		if (salesOrder.Status=="Transmitted Order"){
			pklStatus.ReadOnly = true;
		}else{
			pklStatus.ReadOnly = false;}
}else{
		dtpOrderDate.ReadOnly=true;
		pklStatus.ReadOnly=true;
}

if (user.Id.ToString().Trim()=="ADMIN") {
	dtpInvoiceDate.ReadOnly 		= false;
	pklType.ReadOnly 				= false;
	txtOrigInvoice.ReadOnly			= false;
	txtAdvacedCommission.ReadOnly	= false;
	txtAdvancedCommission2.ReadOnly	= false;
}
else{
	dtpInvoiceDate.ReadOnly 		= true;
	pklType.ReadOnly 				= true;
	txtOrigInvoice.ReadOnly			= true;
	txtAdvacedCommission.ReadOnly	= true;
	txtAdvancedCommission2.ReadOnly	= true;
}

if (salesOrder.Billtoaccount !=null){
using (NHibernate.ISession session2 = new Sage.Platform.Orm.SessionScopeWrapper()){
        		string sql2 = "select ACCOUNTINGSYSTEMID from accountinginfo where Accountid = '"+salesOrder.Billtoaccount.ToString()+"'";
				string AcctNo = session2.CreateSQLQuery(sql2).UniqueResult<string>();		
				txtBillToAccTNO.Text = AcctNo;
				} 
}

if (pklStatus.PickListValue=="Accepted Order" && this.dtpOrderDate.Text==null){
	this.dtpOrderDate.DateTimeValue = DateTime.Now;
}

if (pklStatus.PickListValue=="Ready to Transmit"){
	dtpInvoiceDate.DateTimeValue=DateTime.Now;
}
