Sage.Entity.Interfaces.ITicket ticket = BindingSource.Current as Sage.Entity.Interfaces.ITicket;

Sage.SalesLogix.Security.SLXUserService usersvc = (Sage.SalesLogix.Security.SLXUserService)Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Security.IUserService>();
Sage.Entity.Interfaces.IUser user = usersvc.GetUser();


ticket.SubmittedByID  = user.Id.ToString();




 Sage.Platform.Application.IEntityHistoryService service = Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Application.IEntityHistoryService>(false);

    if (service != null)
    {
		bool flag = false;
        foreach (Sage.Platform.Application.EntityHistory history in service){

		Sage.Entity.Interfaces.ISalesOrder saleso;
		Sage.Entity.Interfaces.IOpportunity opp;
			if (!string.IsNullOrEmpty(history.EntityId.ToString()) && (history.EntityId.ToString() != Sage.Platform.Orm.Entities.EntityViewMode.Insert.ToString()))
			{
    			history.EntityType.Equals(typeof(Sage.Entity.Interfaces.IUser));
			    switch (history.EntityType.Name.ToUpper())
                {
                    case "ISALESORDER":
					    saleso = Sage.Platform.EntityFactory.GetRepository<Sage.Entity.Interfaces.ISalesOrder>().Get(history.EntityId);
						if ((saleso != null) && (saleso.Account != null))
						{
							ticket.Account = saleso.Account;
							ticket.Contact = Sage.SalesLogix.BusinessRules.BusinessRuleHelper.GetPrimaryContact(ticket.Account);
							ticket.SalesOrderNoID = saleso.DocumentId;

						}
						flag = true; //Only First Record of History
						break;

					case "IOPPORTUNITY":
					    opp = Sage.Platform.EntityFactory.GetRepository<Sage.Entity.Interfaces.IOpportunity>().Get(history.EntityId);
						if (opp != null) 
						{
							ticket.EventIDID  = opp.OpportunityCIUDF.Meeting_Id;
							ticket.EventNameID = opp.Id.ToString();


						}
						flag = true; //Only First Record of History
						break;
					default:
						flag = true; //Only First Record of History
						break;
				}
			}
			if (flag) break;
		}
	}

if (!string.IsNullOrEmpty(lueAccount.LookupResultValue.ToString())){
	ticket.AccountNo = ticket.Account.Accountinginfo.AccountingsystemId;
	txtAccountNo.Text = ticket.Account.Accountinginfo.AccountingsystemId.ToString();
}

if (lkpEventID.LookupResultValue !=null) {
	Sage.Entity.Interfaces.ITicket ticket = this.BindingSource.Current as Sage.Entity.Interfaces.ITicket;
	NHibernate.ISession _session = new Sage.Platform.Orm.SessionScopeWrapper();
	string strSQL = "select opportunityid from opportunity_ci_udf where meeting_id='"+lkpEventID.LookupResultValue.ToString()+"'";
	string oppid = _session.CreateSQLQuery(strSQL).UniqueResult<string>();

	if (!string.IsNullOrEmpty(oppid)){
		ticket.EventNameID = oppid;
	}

}

if (lkpSalesOrderNo.LookupResultValue !=null){
	Sage.Entity.Interfaces.ITicket ticket = BindingSource.Current as Sage.Entity.Interfaces.ITicket;
	ticket.SalesOrderNoID = lkpSalesOrderNo.LookupResultValue.ToString();
}
